import React from 'react';
import { useState, useCallback} from 'react';


function Carousel(props) {


    const [counter, setCounter] = useState(0);



    function handleBefore(e){
        e.preventDefault();
        const carouselSlide = document.querySelector(".carousel-slide")
        const carouselImages = document.querySelectorAll(".img");
        let size = carouselImages[0].clientWidth + 16;
        carouselSlide.style.transition = "transform 0.75s ease-in-out"


        setCounter(counter - 1)


        setTimeout(
            () => {

                if (counter < 0) {
                    carouselSlide.style.transform = "translateX(" + ((-size * (testPhotos.length - 1))) + "px)";
                    setCounter({ counter: testPhotos.length - 1 });
                }

                else {
                    carouselSlide.style.transform = "translateX(" + ((-size * counter)) + "px)";
                }
            }, 0 )

    }

    function handleNext(e){
        e.preventDefault();
        const carouselSlide = document.querySelector(".carousel-slide")
        const carouselImages = document.querySelectorAll(".img");
        let size = carouselImages[0].clientWidth + 16;
        carouselSlide.style.transition = "transform 0.75s ease-in-out"


        setCounter(counter - 1)


        setTimeout(
            () => {

                if (counter < 0) {
                    carouselSlide.style.transform = "translateX(" + ((-size * (testPhotos.length - 1))) + "px)";
                    setCounter({ counter: testPhotos.length - 1 });
                }

                else {
                    carouselSlide.style.transform = "translateX(" + ((-size * counter)) + "px)";
                }
            }, 0)

    }

    const {photos} = props;

    const testPhotos = [
        "https://s3-media2.fl.yelpcdn.com/bphoto/CPc91bGzKBe95aM5edjhhQ/o.jpg",
        "https://s3-media4.fl.yelpcdn.com/bphoto/FmXn6cYO1Mm03UNO5cbOqw/o.jpg",
        "https://s3-media4.fl.yelpcdn.com/bphoto/HZVDyYaghwPl2kVbvHuHjA/o.jpg"
    ]

    return ( 
        <div className="carousel">
            <div className="carousel-container">
                <div className="arrows">
                    <div className="prev-button">
                        <button onClick={handleBefore} id="prev">&#10094;</button>
                    </div>
                    <div className="next-button">
                        <button onClick={handleNext} id="next">&#10095;</button>
                    </div>
                </div>
                <div className="carousel-slide">
                    <img src={testPhotos[testPhotos.length - 1]} className="img" id="last-clone" />
                    {testPhotos.map((url, i) => (<img key={i} className="img" src={url} />))}
                    <img src={testPhotos[0]} className="img" id="first-clone" />
                </div>
                
            </div>
                
        </div>
    )



}


export default Carousel;
