import React from "react";
import "./modal.scss";
import "../carousel/carousel_test";
import Carousel from "../carousel/carousel"

const Modal = (props) => {
    debugger
    if (!props.modal){
        return null
    }

    debugger
    const carouselModal = (
      <div className="carousel-modal" onClick={(e) => e.stopPropagation()}>
        <div className="carousel-container">
            <Carousel/>
          {/* <Carousel
            renderCenterLeftControls={({ previousSlide }) => (
              <button id="carousel-btn" onClick={previousSlide}>
                &#10094;
              </button>
            )}
            renderCenterRightControls={({ nextSlide }) => (
              <button id="carousel-btn" onClick={nextSlide}>
                &#10095;
              </button>
            )}
          >
            {props.data.yelpData.photos.map((url, i) => (
              <img key={i} className="carousel-img" src={url}></img>
            ))}
          </Carousel> */}
        </div>
      </div>
    );


    return (
    
        <div className="modal-backdrop" onClick={props.closeModal}>
            {carouselModal}
        </div>

    );


}


export default Modal;
